sidebarCSS:
#root.root {--active-el-shadow: 0 1px 4px -1px rgba(0,0,0,0.282), inset 0 0 0 1px rgba(255,255,255,0.031);}
#root.root {--bookmarks-bookmark-font: var(--tabs-font);}
#root.root {--bookmarks-folder-font: var(--tabs-font);}
#root.root {--ctx-menu-font: var(--tabs-font)}
#root.root {--frame-bg: #00000000;}
#root.root {--general-margin: 0px ;}
#root.root {--nav-btn-active-shadow: none;}
#root.root {--nav-btn-border-radius: 6px;}
#root.root {--nav-btn-width: 32px;}
#root.root {--nav-btn-height: 32px;}
#root.root {--nav-btn-margin: 0px;}
#root.root {--ntb-border-radius: var(--tabs-border-radius);}
#root.root {--tabs-activated-bg: light-dark(#fff, #ffffff33);}
#root.root {--tabs-border-radius: 12px;}
#root.root {--tabs-close-btn-margin: 5px;}
#root.root {--tabs-count-font: var(--tabs-font);}
#root.root {--tabs-font: 500 0.875rem -apple-system;}
#root.root {--tabs-height: 36px;}
#root.root {--tabs-inner-gap: 12px;}
#root.root {--tabs-margin: 5px;}
#root.root {--tabs-pinned-height: 48px;}
#root.root {--tabs-pinned-width: 100%;}
#root.root {--toolbar-bg: #00000000;}

.Tab {
  width: inherit;
  margin: 3px 0px;
}

.Tab .t-box {
  margin-right: 12px;
}

.Tab .title,
.Tab[data-active="true"] .title {
  background: linear-gradient(90deg, var(--tabs-normal-fg) 70%, #f1f1f100 95%);
  background-clip: text;
  color: transparent;
}

.Tab .body {
  border-radius: var(--tabs-border-radius);
}

.Tab[data-pin="true"] .body {
  background-color: light-dark(rgba(70, 70, 70, .1), rgba(170, 170, 170, .2));
  box-shadow: none;
}

.Tab[data-pin="true"] .body:before {
  transition: background-color var(--d-swift);
}

.Tab[data-pin="false"] .fav {
  margin-right: 8px;
}

.Tab .close-icon {
  top: calc(50% - 12px);
  left: calc(50% - 12px);
  opacity: 1;
  width: 24px;
  height: 24px;
}

.Tab .ctx {
  display: none;
}

#root[data-pinned-tabs-position="panel"] .PinnedTabsBar {
  padding: 0 0px var(--tabs-margin) 0px;
  column-gap: 8px;
  row-gap: 8px;
  order: -2;
}

.PinnedTabsBar .tab-wrapper {
  flex: 0 1 calc(33.333% - 3px - 3px);
  box-sizing: border-box;
}

.TabsPanel .new-tab-btns {
  order: -1;
  padding: 3px 0 8px 0;
}

.TabsPanel .new-tab-btns .new-tab-btn {
  width: calc(100% - var(--tabs-margin) * 2);
  height: var(--tabs-height);
  justify-content: start;
  white-space: nowrap;
  overflow: hidden;
}

.TabsPanel .new-tab-btns .new-tab-btn > svg,
.TabsPanel .new-tab-btns .new-tab-btn > img {
  position: static;
  margin: 0 calc(var(--tabs-inner-gap) + 2px) 0 var(--tabs-inner-gap);
  flex-shrink: 0;
}

.TabsPanel .new-tab-btns .new-tab-btn > svg.-badge {
  width: 16px;
  height: 16px;
  padding: 3px;
}

.TabsPanel .new-tab-btns .new-tab-btn > svg.-icon,
.TabsPanel .new-tab-btns .new-tab-btn > img.-icon,
.TabsPanel .new-tab-btns::after {
  display: none;
}

.TabsPanel .new-tab-btns .new-tab-btn::after {
  content: "New Tab";
  font: var(--tabs-font);
  color: var(--tabs-normal-fg);
}

.NavigationBar {
  order: -2;
  margin-left: 57px;
}

.NavigationBar .nav-item,
.Tab[data-pin="true"] .body:before {
  transition: background-color var(--d-fast);
}

.NavigationBar .nav-item[data-active=true] {
  background: none;
}

.NavigationBar .nav-item .bookmarks-badge-icon + .icon {
  clip-path: none;
}

.NavigationBar .nav-item .bookmarks-badge-icon {
  display: none !important;
}

.NavigationBar .nav-item[data-active=true] > .icon,
.NavigationBar .nav-item:not([data-class="space"]):hover > .icon {
  opacity: 0.85;
}

.NavigationBar .nav-item[data-active=false] > .icon,
.NavigationBar .nav-item[data-class="btn"] .icon {
  opacity: 0.33;
}

.ScrollBox .top-shadow {
  left: 0px;
  top: 0px;
  box-shadow: none;
  border-top: 1px solid color-mix(in srgb, var(--frame-fg) 20%, transparent);
}

.ScrollBox .bottom-shadow {
  left: 0px;
  bottom: 0px;
  box-shadow: none;
  border-bottom: 1px solid color-mix(in srgb, var(--frame-fg) 20%, transparent);
}

.top-horizontal-box {
  order: 3 !important;
}

